    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {                                           
        Object ColorSelected = jComboBox1.getSelectedItem();
        if (ColorSelected == "Red"){
            jTextArea1.setBackground(jButton1.getBackground());
        }
        else if (ColorSelected == "Orange"){
            jTextArea1.setBackground(jButton2.getBackground());
        }
        else if (ColorSelected == "Green"){
            jTextArea1.setBackground(jButton3.getBackground());
        }
        else {
            jTextArea1.setBackground(jButton4.getBackground());
            
    }
    }                                          
    
    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        jTextArea1.setBackground(Color.WHITE);
    }                                        

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        addHoverEffect(jButton2, Color.ORANGE);
    }                                        

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        addHoverEffect(jButton3, Color.GREEN);
    }                                        

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        addHoverEffect(jButton1, Color.RED);
    }                                        

