    // Method to add hover effect to a button
    private void addHoverEffect(JButton button, Color hoverColor) {
        button.addMouseListener(new java.awt.event.MouseAdapter() {
        @Override
        public void mouseEntered(java.awt.event.MouseEvent evt) {
            jTextArea1.setBackground(hoverColor);  // Changes the panel's background color
        }

        @Override
        public void mouseExited(java.awt.event.MouseEvent evt) {
            jTextArea1.setBackground(Color.GRAY);  // Resets to the default color
        }
        });
    }

    
    
